import React from 'react'
import { observer } from 'mobx-react'
import { useTranslation } from 'react-i18next'
import ListInfoObserver from '../List/mbox'
import Image from 'next/image'
import { useRouter } from 'next/router'
import { formateDate } from '@/utils'
import { log } from 'next-axiom'

import { Drawer as AntDrawer, Divider, Input, Button, Tooltip, message } from 'antd'
import { CopyOutlined, DownloadOutlined } from '@ant-design/icons'
import { CopyToClipboard } from 'react-copy-to-clipboard'

import LottieFile from '@/components/LottieFile'
import FrameIcon from '@/components/FrameIcon'

import styles from './index.module.scss'

const errorImg =
  'data:image/png;base64,'

const GiveStar = () => {
  const { t } = useTranslation('home')

  const handleStart = () => {
    window.open('https://github.com/Ewall1106/bamboo-material')
  }

  return (
    <div className={styles.giveMeStare} onClick={handleStart}>
      <LottieFile path="https://assets4.lottiefiles.com/packages/lf20_083h7wcs.json" />
      {t('give-me-star')}
    </div>
  )
}

const Drawer = observer(() => {
  const { t } = useTranslation()
  const router = useRouter()
  const iszh = router.locale === 'zh'

  const visible = ListInfoObserver.getDrawerShow()
  const setDrawerShow = ListInfoObserver.setDrawerShow
  const info: any = ListInfoObserver.getCurrentItem()

  const DownloadCount = ({ count }) => {
    return (
      <div className={styles.drawer__download}>
        <span>{t('download')}: </span>
        <DownloadOutlined />
        <span style={{ paddingLeft: 2 }}>{count}</span>
      </div>
    )
  }

  return (
    <AntDrawer
      className={styles.drawer}
      width={480}
      placement="right"
      onClose={() => setDrawerShow(false)}
      closable={false}
      open={visible}
      footer={<GiveStar />}
    >
      <div className={styles.drawer__content}>
        <div className={styles.drawer__thumb}>
          <Image
            alt="thumb"
            width={374}
            height={200}
            objectFit="contain"
            src={info.thumb || errorImg}
          />
        </div>
        <Divider />
        <div className={styles.drawer__title}>{iszh ? info.name_zh : info.name}</div>
        <div className={styles.drawer__desc}>{iszh ? info.desc_zh : info.desc}</div>
        <div className={styles.drawer__framework}>
          {t('framework')}: <FrameIcon type={info.type} /> ({info.type})
        </div>
        <DownloadCount count={info.download} />
        <div className={styles.drawer__create}>
          {t('create')}: {formateDate(info.createdAt)}
        </div>
        <div className={styles.drawer__update}>
          {t('update')}: {formateDate(info.updatedAt)}
        </div>

        <div className={styles.drawer__command}>
          <Input style={{ flex: 1 }} defaultValue={`$ npm init bamboo create ${info._id}`} />
          <CopyToClipboard
            text={`npm init bamboo create ${info._id}`}
            onCopy={() => {
              log.info('command copy button click')
              message.success(t('copy_success'))
            }}
          >
            <Button icon={<CopyOutlined />} />
          </CopyToClipboard>
        </div>
      </div>
    </AntDrawer>
  )
})

export default Drawer
